mapper.insert:

	select drug_apply_seq.nextval from dual

insert into drug_apply(
	da_nm, da_state, da_date, da_price
) values(
	${daNm}, 'N', sysdate, ${daPrice}
)

mapper.update:
update da_detail
set
	da_dt_check = #{daDtCheck}
where
	da_dt_nm = #{daDtNm}

	update drug_apply
    set
        da_state = #{daState}
    where
        da_nm = #{daNm}
